function gotobookapp(e) {
    var t = getParam("id"); 
    console.log('111111' + t)
    if(t == '' || t == '1') {
        window.location.href = "http://d.dyreader.cn/apk/com.dotreader.dnovel-bdfeedst.apk";
    } else {
        window.location.href = "http://d.dyreader.cn/apk/com.dotreader.dnovel-bdsearchst" + t + ".apk";
    }
}
function getParam(name) {
    var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)");
    var r = window.location.search.substr(1).match(reg);
    if (r != null)
        return decodeURI(r[2]);
    return '';
}